mapper.insert:
			/* 배송지 등록 */
			insert into addr_manage
			(shipping_addr_code
			 ,shipping_addr_postal
			 ,shipping_addr_road
			 ,shipping_addr_loca
			 ,shipping_addr_detail
			 ,shipping_addr_name
			 ,shipping_addr_recipient
			 ,shipping_addr_tel
			 ,shipping_addr_phone
			 ,shipping_addr_seq
			 ,member_id
			 ,shipping_addr_reg_date)
			VALUES
			(#{addrCode}
			,#{addrPostal}
			,#{addrRoad}
			,#{addrLoca}
			,#{addrDetail}
			,#{addrName}
			,#{addrRecipient}
			,#{addrTel}
			,#{addrPhone}
			,#{addrSeq}
			,#{memberId}
			,CURDATE());

mapper.delete:
		/* 배송지 삭제 */
		DELETE
		FROM
		 	addr_manage
		WHERE

			shipping_addr_code = #{addrCode};

mapper.select:
				/* 전체 회원 배송지 조회 */
				select 
				 *
				from addr_manage;
