mapper.insert:
			/* 로그인 기록 등록 */
			insert into login_history
				( login_code
				 ,login_date
				 ,logout_date
				 ,member_id
				 ,ipv4_address)
			VALUES
				(#{loginCode}
				,CURDATE()
				,CURDATE()
				,#{memberId}
				,'1234');

mapper.delete:
		/* 로그인 기록 삭제 */
		DELETE
		FROM
		 	login_history 
		WHERE

			login_code = #{loginCode};

mapper.select:
				/* 전체 회원 로그인 기록 조회 */
				select 
				 *
				from login_history;
