mapper.update:
UPDATE member_info SET
company_code = #{companyCode},
position_level_code = 'level_code_3' WHERE member_id = #{memberId};

mapper.insert:
			/* 회원 등록 */
			insert into member_info
			(member_id,member_pw,member_name,member_phone,member_email,position_level_code,company_code,member_reg_date,member_status)
			VALUES
			(#{memberId},#{memberPw},#{memberName},#{memberPhone},#{memberEmail},#{positionLevelCode},#{companyCode},CURDATE(),'normal');

mapper.delete:
/* 회원 삭제 */
DELETE
FROM
 	member_info 
WHERE
	member_id = #{memberId};

mapper.update:
/* 업체직위수정 */
UPDATE
	member_info
SET
	position_level_code = #{positionLevelCode}
WHERE
	member_id = #{memberId};
