SELECT p.production_code AS productionCode, p.company_code AS companyCode, p.farm_code AS farmCode, p.expected_cage_production_code AS expectedCageProductionCode, DATE_FORMAT(p.real_harvest_day, '%Y-%m-%d')AS realHarvestDay, p.real_production AS realProduction, p.loss_rate AS lossRate, p.member_id AS memberId, p.production_reg_date AS productionRegDate, ex.calculation_standard_code AS calculationStandardCode FROM production AS p INNER JOIN cage_cycle AS ex ON p.expected_cage_production_code = ex.expected_cage_production_code
p.company_code = #{companyCode}
AND DATE_FORMAT(p.real_harvest_day, '%Y-%m-%d') BETWEEN #{fromDate} AND #{toDate}
AND (${searchKey} LIKE CONCAT('%', #{searchValue}, '%'))
AND DATE_FORMAT(p.real_harvest_day, '%Y-%m-%d') >= #{fromDate}
AND (${searchKey} LIKE CONCAT('%', #{searchValue}, '%'))
AND DATE_FORMAT(p.real_harvest_day, '%Y-%m-%d') <= #{toDate}
AND (${searchKey} LIKE CONCAT('%', #{searchValue}, '%'))
AND (${searchKey} LIKE CONCAT('%', #{searchValue}, '%'))
ORDER BY real_harvest_day DESC;

SELECT p.production_code AS productionCode, p.company_code AS companyCode, p.farm_code AS farmCode, p.expected_cage_production_code AS expectedCageProductionCode, DATE_FORMAT(p.real_harvest_day, '%Y-%m-%d')AS realHarvestDay, p.real_production AS realProduction, p.loss_rate AS lossRate, p.member_id AS memberId, p.production_reg_date AS productionRegDate, ex.calculation_standard_code AS calculationStandardCode FROM production AS p INNER JOIN cage_cycle AS ex ON p.expected_cage_production_code = ex.expected_cage_production_code
p.farm_code = #{farmCode}
ORDER BY real_harvest_day DESC;

SELECT p.production_code AS productionCode, p.company_code AS companyCode, p.farm_code AS farmCode, p.expected_cage_production_code AS expectedCageProductionCode, DATE_FORMAT(p.real_harvest_day, '%Y-%m-%d')AS realHarvestDay, p.real_production AS realProduction, p.loss_rate AS lossRate, p.member_id AS memberId, p.production_reg_date AS productionRegDate, ex.calculation_standard_code AS calculationStandardCode FROM production AS p INNER JOIN cage_cycle AS ex ON p.expected_cage_production_code = ex.expected_cage_production_code
p.company_code = #{companyCode}
ORDER BY real_harvest_day DESC;

SELECT c.cage_volume as cageVolume, c.cage_num as cageNum, c.cage_total cageTotal, cy.expected_cage_production_code AS cycleCode, cy.company_code AS companyCode, cy.farm_code AS farmCode, cy.cage_code AS cageCode, Date(harvest_start_date) AS harvestStartDate, Date(estimated_harvest_date) AS estimatedHarvestDate, input_egg AS inputEgg, calculation_standard_code AS calculationStandardCode, estimated_production AS estimatedProduction, cy. member_id AS memberId, estimated_production_reg_date AS estimatedProductionRegDate, DATEDIFF(estimated_harvest_date, NOW()) AS dayDiffHarvest FROM cage_cycle AS cy INNER JOIN cage AS c ON cy.cage_code = c.cage_code
cy.farm_code = #{farmCode}
AND DATEDIFF(estimated_harvest_date, NOW()) = #{searchValue}
AND (${searchKey} LIKE CONCAT('%', #{searchValue}, '%'))
AND DATE_FORMAT(estimated_harvest_date, '%Y-%m-%d') BETWEEN #{fromDate} AND #{toDate}
AND DATE_FORMAT(estimated_harvest_date, '%Y-%m-%d') >= #{fromDate}
AND DATE_FORMAT(estimated_harvest_date, '%Y-%m-%d') <= #{toDate}
